def extract_tag(child):    """    :param child: the child (root[1][i]) of an xml file as downloaded from www.food.co.uk    :return: gets the tags to be used as column names of the data frame    """    tags = []    for i in range(0, len(child), 1):        if len(child[i]) == 0:            tags.append(child[i].tag)        else:            child1 = child[i]            temp = extract_tag(child1)            for j in range(0, len(temp),1):                tags.append(temp[j])    return tagsdef extract_content(child):    """    :param child: the child (root[1][i]) of an xml file as downloaded from www.food.co.uk    :return: gets the content to be used as values of the data frame    """    content = []    for i in range(0, len(child), 1):        if len(child[i]) == 0:            content.append(child[i].text)        else:            child1 = child[i]            temp = extract_content(child1)            for j in range(0,len(temp),1):                content.append(temp[j])    return contentdef make_df(tag_list, content_list, colnames):    """    :param tag_list: the output of the function extract_tag()    :param content_list: the output of the function extract_content()    :param colnames: the full list of columns names available from the xmls    :return: gets the tags to be used as column names of the data frame    """    import pandas as pd    df_final_temp = pd.DataFrame(columns=[colnames], index=[0])    df_temp = pd.DataFrame(data=[content_list])    df_temp.columns = tag_list    for colname in df_final_temp.columns:        if colname in df_temp:            df_final_temp[colname][0] = df_temp[colname][0]        else:            df_final_temp[colname][0] = None    return df_final_temp